'use strict';

// The time that the coutdown will end on.
let targetDate = new Date('2021-04-30T00:00:00');

// This text will be shown for non-recurring countdowns after they concluded.
//let finalMessage = 'THE DAY IS HERE!';

/**
 * @param {String} value (for property 'text')
 */
var countdownDate;

export function update(value) {
	targetDate = new Date(countdownDate);
	var currentDate = new Date();
	var diff = targetDate.getTime() - currentDate.getTime();
	if (diff < 0) {
		diff *= -1;
	}

	var years = Math.floor(diff / (1000 * 60 * 60 * 24 * 365));
	var days = Math.floor(diff / (1000 * 60 * 60 * 24));
	var hours = Math.floor(diff / (1000 * 60 * 60)) % 24;
	var minutes = Math.floor(diff / (1000 * 60)) % 60;

	var seconds = Math.floor(diff / (1000)) % 60;

	value = ""; 

	seconds = ("0" + seconds).slice(-2);
	minutes = ("0" + minutes).slice(-2);
	hours = ("0" + hours).slice(-2);

	if(days < 10)
		days = ("0" + days).slice(-2);

	value += days + "   " + hours + "   " + minutes + "   " + seconds;  //countdownDate + '\n';
	return value;
}

/**
 * @param {String} value (for property 'text')
 */
export function init(value) {
	countdownDate = value;
	return value;
}

